// ignore_for_file: prefer_const_constructorsimport 'dart:async';import 'dart:convert';import 'package:flight_tracker/airports/screen/airport_screen_detail.dart';import 'package:flight_tracker/app_theme/color.dart';import 'package:flight_tracker/app_theme/reusing_widgets.dart';import 'package:flight_tracker/app_theme/theme_texts.dart';import 'package:flutter/material.dart';import 'package:flutter/services.dart';class AirportsScreen extends StatefulWidget {  AirportsScreen({super.key});  @override  State<AirportsScreen> createState() => _AirportsScreenState();}class _AirportsScreenState extends State<AirportsScreen> {  TextEditingController searchAirportController = TextEditingController();  List _items = [];  @override  void initState() {    super.initState();    setState(() {      readJson();    });  }  Future<void> readJson() async {    final String response = await rootBundle.loadString('assets/jsons/airport.json');    final data = await json.decode(response);    setState(() {      _items = data["response"];    });  }  @override  Widget build(BuildContext context) {    return Scaffold(      backgroundColor: ColorsTheme.primaryColor,      body: SafeArea(        child: Container(          color: ColorsTheme.white,          child: Column(            crossAxisAlignment: CrossAxisAlignment.start,            children: [              Container(                color: ColorsTheme.primaryColor,                padding: EdgeInsets.all(12),                child: TextFormField(                  controller: searchAirportController,                  enableInteractiveSelection: false,                  style:                  ThemeTexts.textStyleTitle2.copyWith(color: Colors.black),                  onChanged: (String value) {                    setState(() {                      // searchAirportController.text = value.toLowerCase();                    });                  },                  decoration: ReusingWidgets.SearchTextFormField(                    hintText: "Search for an Airport",                  ),                ),              ),              Expanded(                child: Container(                  color: Colors.white,                  child: Column(                    children: [                      Flexible(                        child: ListView.builder(                          padding: EdgeInsets.all(5),                          itemCount: _items.length,                          itemBuilder: (context, index) {                            String cityName = _items[index]["iata_code"]  ?? "---";                            // String cityShortName = snapshot.data!.request!.client!.geo!.continent  ?? "---";                            String countryShortName = _items[index]["country_code"] ?? "---";                            String airportName = _items[index]["name"] ?? "---";                            String iataValue = _items[index]["iata_code"]  ?? "---";                            return                              countryShortName.toLowerCase().contains(searchAirportController.text) ||                              airportName.toLowerCase().contains(searchAirportController.text) ?                              // airportName.where((a) => (a.dataTitle.toLowerCase().contains(searchAirportController.text.toLowerCase().trim()))).toList()                              InkWell(                                  onTap: () async {                                    Navigator.push(context, MaterialPageRoute(builder: (context){                                      return AirportScreenDetail(airportName: airportName,iataValue: iataValue,);                                    }));                                  },                                  child: ListTile(                                    title: Text(                                      "$cityName,"                                          " $countryShortName",                                      style: ThemeTexts.textStyleValueBlack,                                    ),                                    // subtitle: Text("$cityShortName -  $airportName",                                    subtitle: Text(airportName,                                        style: ThemeTexts.textStyleValueBlack2),                                    trailing: FlutterLogo(                                      size: 30,                                      textColor: Colors.blue,                                      style: FlutterLogoStyle.stacked,                                    ), //F                                  )) : Container();                          },                        ),                      ),                    ],                  ),                ),              ),            ],          ),        ),      ),    );  }}